var Class = require('../core/class');

module.exports = Class({
  grab: function grab() {
    for (var i = 0; i < arguments.length; i++) {
      arguments[i].inject(this);
    }

    return this;
  },
  empty: function empty() {
    var node;

    while (node = this.firstChild) {
      node.eject();
    }

    return this;
  }
});